import { Component, Input, OnChanges, OnInit, SimpleChanges } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { StudentsService } from '../../services/students.service';
import { Teacher } from '../../models/teacher';

@Component({
  selector: 'app-add-subject',
  templateUrl: './add-subject.component.html',
  styleUrls: ['./add-subject.component.css']
})
export class AddSubjectComponent implements OnInit{
  formSub:any;
  selectedTeacher:any;
  gradeId:any
  teachers:Teacher[]=[];
  constructor(private fb:FormBuilder, private studentService:StudentsService){}
  ngOnInit()
  {
    this.studentService.diableSubjectForm.subscribe(x=>{
      if(x)
        this.formSub.disable();
      else
        this.formSub.enable();
    })
    this.studentService.mySubject.subscribe(x=>{
      this.getTeachers();
    })
    this.studentService.mySubject.subscribe(x=>{
      console.log(111)
    })
    this.initiateForm();
  }
  initiateForm()
  {
    this.formSub= this.fb.group({
      name:['', Validators.required],
      teacher:['',Validators.required],
    })

  }

  getTeachers()
  {
    this.gradeId = this.studentService.gradeId
    this.studentService.getTeachers(this.gradeId).subscribe(response=>{
      this.teachers = response.data;
    },error=>{
      console.log(error);
    })
  }
}
